if (globalVars("captchaStartAlreadyRan") == null) {
    const request = require("request");
    const Discord = require('discord.js');
    const fs = require('fs');
    var checkUpdates = true;

    this.storeValue(true, 3, "captchaStartAlreadyRan", cache);

    if (!fs.existsSync("./captchas")) {
        fs.mkdirSync("./captchas");
    }

    fs.readdir("./captchas", (err, files) => {
        if (err) throw err;

        for (const file of files) {
            var fileExt = getExtension(file);
            if (fileExt == ".png") {
                fs.unlink(`./captchas/${file}`, err => {
                    if (err) throw err;
                });
            }
        }
    });

    function getExtension(filename) {
        var i = filename.lastIndexOf('.');
        return (i < 0) ? '' : filename.substr(i);
    }

    var requiredIntents = ["GUILD_MEMBERS"];
        var intentCheck = new Discord.Intents(DBM.Bot.intents()).toArray();
        requiredIntents.forEach(intent => {
            if (!intentCheck.includes(intent)) {
                console.log(`[Captcha] You don't have the ${intent} intent enabled!`)
            }
        })



    if (checkUpdates) {
        request('https://raw.githubusercontent.com/ItzPike/DBM-Rawdata-Releases/master/Captcha/versionCheck.json', function(error, response, body) {
            if (response.statusCode == 200) {
                var allVersions = JSON.parse(body);

                var commandVersions = {};
                var eventVersions = {};
                var commandKeys = [];
                var eventKeys = []

                allVersions["commands"].forEach(commandVer => {
                    commandVersions[commandVer[0]] = commandVer[1];
                    commandKeys.push(commandVer[0]);
                });

                allVersions["events"].forEach(eventVer => {
                    eventVersions[eventVer[0]] = eventVer[1];
                    eventKeys.push(eventVer[0])
                })

                fs.readFile("./data/commands.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemCommands = await data.filter(command => command != null && commandKeys.includes(command["_id"]));

                    for (i = 0; i < systemCommands.length; i++) {
                        var systemActionFind = await systemCommands[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemActionFind.length != 0) {
                            var versionCheckSplit = systemActionFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit[1] != null && versionCheckSplit[1] != commandVersions[systemCommands[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Capcha] The command ${systemCommands[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })

                fs.readFile("./data/events.json", async function(err, data) {
                    var data = JSON.parse(data);

                    var systemEvent = await data.filter(event => event != null && eventKeys.includes(event["_id"]));

                    for (i = 0; i < systemEvent.length; i++) {
                        var systemEventFind = await systemEvent[i]["actions"].filter(actionInfo => actionInfo.name = "Run Script" && actionInfo.code != null);

                        if (systemEventFind.length != 0) {
                            var versionCheckSplit = systemEventFind[0].code.split("VersionCheck:");
                            if (versionCheckSplit != null && versionCheckSplit[versionCheckSplit.length - 1] != eventVersions[systemEvent[i]["_id"]] && !isNaN(versionCheckSplit[versionCheckSplit.length - 1])) {
                                console.log(`[Capcha] The event ${systemEvent[i]["name"]} needs an update!`)
                            }
                        }
                    }
                })
            }
        });
    }

    client.on('guildMemberAdd', (member) => {
        var memberJoined = member;
        var serverRoleToGive = memberJoined.guild.data("serverCaptchaRole");
        var roleToGive = [];

        if (serverRoleToGive != null && serverRoleToGive != 0) {
            if (!Array.isArray(serverRoleToGive)) {
                serverRoleToGive = [serverRoleToGive];
            } else {
                serverRoleToGive = serverRoleToGive;
            }

            var rolesInServer = memberJoined.guild.roles.cache.keyArray();

            for (i=0; i<serverRoleToGive.length; i++) {
                if (rolesInServer.includes(serverRoleToGive[i])) {
                    roleToGive.push(serverRoleToGive[i]);
                }
            }

            if (roleToGive.length > 0 || serverRoleToGive == 1) {
                getCaptcha(3, "");
            } else {
                memberJoined.guild.setData("serverCaptchaRole", 0);
            }

            if (serverRoleToGive != roleToGive) {
                memberJoined.guild.setData("serverCaptchaRole", roleToGive)
            }
        }

        function getCaptcha(attempts, reason) {
            var embed = {
                "title": "Automated Captcha System",
                "description": `Welcome to ${memberJoined.guild.name}!\n\nThis server is protected by a captcha system and you are required to solve the below captcha.\n\n**Captchas are case sesitive.**\n\nIf you answer wrong, you have three attempts in total to try to get it right.\nThe captcha will refresh after every failed attempt.\n\nYou have 120 seconds for each captcha\n\n${attempts} Attempts Left\n\n${reason}`,
                "color": 0x00ff00
            }

            if (attempts != 0) {
                request('https://captchaGenerate.itzpike.repl.co', function(error, response, body) {
                    if (response.statusCode == 200) {
                        body = JSON.parse(body);

                        var data = body.image.replace(/^data:image\/\w+;base64,/, '');
                        var fileTitle = Math.random().toString(36).substring(6);
                        fs.writeFile(`./captchas/${fileTitle}.png`, data, {
                            encoding: 'base64'
                        }, function(err) {
                            if (err) {
                                console.log(err);
                            }

                            const file = new Discord.MessageAttachment(`./captchas/${fileTitle}.png`);
                            embed["image"] = {
                                "url": `attachment://${fileTitle}.png`
                            }

                            memberJoined.send({
                                files: [file],
                                embed: embed
                            }).then(msgSent => {
                                msgSent.channel.awaitMessages(m => m.author.id == memberJoined.id, {
                                        max: 1,
                                        time: 120000,
                                        errors: ['time']
                                    })
                                    .then(collected => {
                                        fs.unlinkSync(`./captchas/${fileTitle}.png`);
                                        msgSent.delete();
                                        if (collected.first().content.replace(/\s/g, '') == body.key) {
                                            if (serverRoleToGive != 1) {
                                                memberJoined.roles.add(roleToGive);
                                            }
                                            embed.description = "Captcha success!\n\nWelcome to the server..";
                                            delete embed["image"];
                                            memberJoined.send({
                                                embed: embed
                                            });
                                        } else {
                                            getCaptcha(attempts - 1, "You did not enter the valid captcha!");
                                        }
                                    })
                                    .catch(err => {
                                        fs.unlinkSync(`./captchas/${fileTitle}.png`);
                                        getCaptcha(attempts - 1, "You had ran out of time!");
                                    });
                            })
                        });
                    }
                })
            } else {
                embed.color = 0xff0000;
                embed.description = "Captcha verfication failed!\n\nYou have ran out of attempts!\nPlease rejoin the server to get the captcha again.";
                memberJoined.send({
                    embed: embed
                }).then(serverMess => {
                    memberJoined.kick();
                })
            }
        }


    })
}

